// src/app/styles/_variables.scss ou src/assets/styles/_variables.scss

// --- Palette de Couleurs Principales ---
$primary-color: #41B3A3;       // Teal/Vert principal (boutons primaires, accents)
$accent-color: #E27D60;        // Coral/Orange (accents secondaires, peut-être certains boutons)
$warn-color: #F44336;          // Rouge (erreurs, suppression) - Renommé depuis $delete-color pour cohérence Material
$success-color: #4CAF50;       // Vert (succès, validation) - Renommé depuis $edit-color pour rôle plus large

// --- Couleurs de Texte ---
$text-color-primary: #333;     // Texte principal (foncé)
$text-color-secondary: #666;   // Texte secondaire (gris moyen)
$text-color-light: #fff;       // Texte sur fonds sombres/colorés

// --- Couleurs de Fond ---
$background-color: #f7f9fc;    // Fond général de la page (très clair)
$container-background: #fff;   // Fond des conteneurs/cartes (blanc)
$input-background: #f0f2f5;    // Fond des champs de saisie (léger)
$date-background: #e9ecef;     // Fond pour éléments spécifiques comme la date
$table-header-background: #f9fafb; // Fond des en-têtes de tableau
$table-row-hover: rgba(0, 0, 0, 0.04); // Survol des lignes de tableau

// --- Bordures et Ombres ---
$border-color: #e0e0e0;         // Couleur de bordure légère
$border-radius: 8px;           // Rayon de bordure standard
$card-shadow: 0 4px 12px rgba(0, 0, 0, 0.08); // Ombre standard pour les cartes

// --- Typographie (Optionnel, à définir si besoin) ---
$font-family-base: 'Poppins', sans-serif;
$font-size-base: 14px;
$font-weight-normal: 400;
$font-weight-medium: 500;
$font-weight-semibold: 600;
$font-weight-bold: 700;

// --- Espacements (Optionnel, pour cohérence) ---
$spacing-xs: 4px;
$spacing-sm: 8px;
$spacing-md: 16px;
$spacing-lg: 24px;
$spacing-xl: 32px;

// --- Couleurs spécifiques aux actions (si vous préférez garder les noms originaux) ---
