+      phone: ['', [Validators.required, Validators.pattern('^[0-9]+$')]], 
+      companyName: [this.defaultCompanyName, Validators.required],
+      address: ['', Validators.required],
+      idCompany: [this.defaultIdCompany, Validators.required]
+    });
+  }
+
