+    const clientData = {
+      name: this.name?.value,
+      address: this.address?.value,
+      address: this.address?.value,
+      idCompany: idCompany,
+      companyName:CompanyName,
+      idAdmin: idAdmin
+    };
+    setTimeout(() => { // Simule un appel asynchrone
+      this.isLoading = false;
+      console.log('Client (simulé) créé:', clientData);
+      this.activeModal.close(clientData); // Renvoyer les données du formulaire
+    }, 1000);
+  }
+
+  cancel(): void {
+    this.activeModal.dismiss('cancel click');
+  }
+
+}

