+.dashboard-container {
+    font-family: 'Poppins', sans-serif;
+    padding: 20px;
+    background: #f9f9f9;
+  
+    h2 {
+      font-size: 20px;
+      font-weight: 600;
+    }
+  
+    /* Task Summary */
+    .task-summary {
+      background: white;
+      padding: 20px;
+      border-radius: 10px;
+      box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
+      margin-bottom: 20px;
+      position: relative;
+  
+      .add-task {
+        position: absolute;
+        top: 20px;
+        right: 20px;
+        background: #fbb;
+        border-radius: 20px;
+        font-weight: 500;
+      }
+  
+      .task-cards {
+        display: flex;
+        gap: 20px;
+        margin-top: 20px;
+  
+        .task-card {
+          flex: 1;
+          padding: 20px;
+          border-radius: 10px;
+          color: white;
+          text-align: center;
+          font-weight: 600;
+          box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
+          display: flex;
+          flex-direction: column;
+          align-items: center;
+          justify-content: center;
+          gap: 10px;
+  
+          i {
+            font-size: 30px;
+          }
+  
+          &.upcoming {
+            background: #fdd;
+          }
+          &.in-progress {
+            background: #99d;
+          }
+          &.completed {
+            background: #ffdb58;
+          }
+        }
+      }
+    }
+  
+    /* Task Activity */
+    .task-activity {
+      background: white;
+      padding: 20px;
+      border-radius: 10px;
+      box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
+  
+      .task-stats {
+        display: flex;
+        gap: 20px;
+        justify-content: space-between;
+        margin-top: 20px;
+  
+        .stat {
+          background: #fff;
+          padding: 15px;
+          border-radius: 10px;
+          flex: 1;
+          text-align: center;
+          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
+          
+          i {
+            font-size: 25px;
+            color: #ffc107;
+          }
+          
+          p {
+            font-size: 14px;
+            color: #666;
+          }
+  
+          h3 {
+            font-size: 18px;
+            margin: 5px 0;
+          }
+        }
+      }
+    }
+  }
+  
