// Utilisez les mÃªmes variables que dans votre exemple
$primary-color: #5CBDB2;
$accent-color: #E27D60;
$warn-color: #F44336;
$cancel-color: #9E9E9E;
$text-color-primary: #333;
$text-color-secondary: #666;
$text-color-light: #fff;
$border-color: #e0e0e0;
$border-color-focus: #5CBDB2;
$border-radius: 12px;
$border-radius-small: 8px;
$spacing-unit: 8px;
$space-xs: $spacing-unit;
$space-sm: $spacing-unit * 2;
$space-md: $spacing-unit * 3;
$space-lg: $spacing-unit * 4;
$space-xl: $spacing-unit * 6;

.email-form-container {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background-color: #f8f9fa;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

.content-container {
  flex: 1;
  padding: $space-xl;
  background-color: white;
  border-radius: $border-radius;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
  margin: $space-md;
}

.email-form {
  display: flex;
  flex-direction: column;
  gap: $space-lg;
}

.form-section {
  display: flex;
  flex-direction: column;
  margin-bottom: $space-md;
}

.form-field-reference {
  label {
    display: block;
    color: $text-color-secondary;
    font-weight: 500;
    margin-bottom: $space-xs;
    font-size: 14px;
  }

  input, textarea {
    width: 100%;
    padding: $space-sm $space-md;
    border: 1px solid $border-color;
    border-radius: $border-radius-small;
    background-color: #f5f5f5;
    font-size: 14px;
    color: $text-color-primary;
    transition: border-color 0.2s ease, box-shadow 0.2s ease;

    &:focus {
      outline: none;
      border-color: $border-color-focus;
      box-shadow: 0 0 0 2px rgba(92, 189, 178, 0.1);
    }

    &::placeholder {
      color: #999;
    }
  }

  textarea {
    min-height: 150px;
    resize: vertical;
  }
}

.error-message {
  color: $warn-color;
  font-size: 12px;
  margin-top: $space-xs;
}

.file-upload-section {
  margin-top: $space-md;

  label {
    display: inline-flex;
    align-items: center;
    gap: $space-xs;
    cursor: pointer;
  }
}

.attachments-list {
  margin-top: $space-sm;
  display: flex;
  flex-direction: column;
  gap: $space-xs;
}

.attachment-item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: $space-xs $space-sm;
  background-color: #f5f5f5;
  border-radius: $border-radius-small;
  font-size: 14px;

  .remove-attachment {
    color: $text-color-secondary;
    width: 24px;
    height: 24px;
    line-height: 24px;

    &:hover {
      color: $warn-color;
    }
  }
}

.form-actions {
  display: flex;
  justify-content: flex-end;
  gap: $space-sm;
  margin-top: $space-xl;
  padding-top: $space-lg;
}

.button-reference {
  padding: $space-sm $space-lg;
  min-height: 44px;
  font-weight: 500;
  border-radius: $border-radius-small;
  font-size: 14px;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: $space-xs;
  transition: all 0.2s ease;

  mat-icon {
    font-size: 18px;
    width: 18px;
    height: 18px;
  }

  &.button-cancel {
    background-color: transparent;
    color: $cancel-color;
    border: 1px solid $cancel-color;

    &:hover {
      background-color: rgba($cancel-color, 0.1);
    }
  }

  &.button-primary {
    background-color: $primary-color;
    color: $text-color-light;

    &:hover {
      background-color: darken($primary-color, 10%);
    }

    &:disabled {
      background-color: #ccc;
      color: #666;
      cursor: not-allowed;
    }
  }

  &.button-secondary {
    background-color: white;
    color: $primary-color;
    border: 1px solid $primary-color;

    &:hover {
      background-color: rgba($primary-color, 0.1);
    }

    &:disabled {
      background-color: #f5f5f5;
      color: #ccc;
      border-color: #ccc;
      cursor: not-allowed;
    }
  }
}

@media (max-width: 768px) {
  .content-container {
    padding: $space-md;
    margin: $space-sm;
  }

  .form-actions {
    flex-direction: column;
    
    button {
      width: 100%;
      justify-content: center;
    }
  }
}
