+    &:not(:first-child):not(:last-child) {
+      text-align: center;
+    }
+
+    &:last-child {
+      text-align: right;
+    }
+  }
+
+  td {
+    padding: 14px 16px;
+    border-bottom: 1px solid $border-color;
+    font-size: 14px;
+    color: $text-color-primary;
+
+    &:not(:first-child):not(:last-child) {
+      text-align: center;
+    }
+
+    &:last-child {
+      text-align: right;
+    }
+  }
+
+  tr {
+    transition: background-color 0.2s ease;
+
+    &:hover {
+      background-color: $background-color;
+    }
+
+    &:last-child td {
+      border-bottom: none;
+    }
+  }
+}
+
+// --- Status Badges ---
+.status-badge {
+  padding: 4px 10px;
+  border-radius: 12px;
+  font-size: 12px;
+  font-weight: 500;
+  color: $text-color-light;
+  text-transform: capitalize;
+  display: inline-block;
+  text-align: center;
+  min-width: 80px;
+
+  &.status-pending { background-color: $pending-color; }
+  &.status-paid { background-color: $paid-color; }
+  &.status-overdue { background-color: $overdue-color; }
+  &.status-draft { background-color: $cancel-color; }
+  &.default { background-color: $text-color-secondary; }
+}
+
+// --- Action Buttons ---
+.action-buttons {
+  display: flex;
+  justify-content: flex-end;
+  gap: 8px;
+
+  button {
+    border: none;
+    background: none;
+    cursor: pointer;
+    font-size: 1.2rem;
+    padding: 6px;
+    border-radius: 50%;
+    width: 36px;
+    height: 36px;
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    transition: all 0.2s ease;
+
+    &:disabled {
+      opacity: 0.5;
+      cursor: not-allowed;
+    }
+
+    &.edit-btn {
+      color: $primary-color;
+      
+      &:hover:not(:disabled) {
+        background-color: rgba($primary-color, 0.1);
+      }
+    }
+
+    &.delete-btn {
+      color: $warn-color;
+      
+      &:hover:not(:disabled) {
+        background-color: rgba($warn-color, 0.1);
+      }
+    }
+  }
+}
+
+// --- Responsive Design ---
+@media (max-width: 768px) {
+  .invoice-header {
+    flex-direction: column;
+    align-items: stretch;
+
+    .actions-container {
+      flex-direction: column;
+      gap: 8px;
+
+      .action-button {
+        width: 100%;
+      }
+    }
+  }
+
+  .invoice-table {
+    display: block;
+    overflow-x: auto;
+  }
+}
