// src/app/invoices/invoice-detail/invoice-detail.component.scss
:host {
  display: block;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; // Adaptez à votre style
  color: #333;
}

.invoice-container {
  max-width: 800px;
  margin: 20px auto;
  padding: 25px;
  background-color: #fff;
  border: 1px solid #e0e0e0;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05); // Un peu d'ombre comme dans votre header
  border-radius: 8px; // Bords arrondis
}

.invoice-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 2px solid #f0f0f0;
  padding-bottom: 15px;
  margin-bottom: 20px;

  h1 {
    margin: 0;
    font-size: 2em;
    color: #2c3e50; // Couleur titre
  }
}

.invoice-actions {
  display: flex;
  gap: 10px;

  .action-btn {
    padding: 8px 15px;
    border: 1px solid #ccc;
    background-color: #f9f9f9;
    color: #333;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.2s, color 0.2s;
    font-size: 0.9em;
    display: inline-flex;
    align-items: center;
    gap: 5px;

    &:hover {
      background-color: #e9e9e9;
    }

    &.primary {
      background-color: #3498db; // Bleu
      color: white;
      border-color: #3498db;
      &:hover {
        background-color: #2980b9;
      }
    }
    &.danger {
      background-color: #e74c3c; // Rouge
      color: white;
      border-color: #e74c3c;
      &:hover {
        background-color: #c0392b;
      }
    }
    i { // Pour les icônes FontAwesome par exemple
      margin-right: 4px;
    }
  }
}


.invoice-meta {
  margin-bottom: 25px;
  line-height: 1.6;
  font-size: 0.95em;

  div {
    margin-bottom: 5px;
  }

  strong {
    color: #555;
  }

  .status {
    padding: 3px 8px;
    border-radius: 4px;
    font-weight: bold;
    text-transform: uppercase;
    font-size: 0.8em;

    &.unpaid {
      background-color: #fef0f0; // Rouge clair
      color: #e74c3c;
    }
    &.paid {
      background-color: #e6f7f0; // Vert clair
      color: #2ecc71;
    }
    &.draft {
      background-color: #f5f5f5; // Gris clair
      color: #7f8c8d;
    }
    // Ajoutez d'autres statuts si nécessaire
  }
}

.invoice-lines {
  margin-bottom: 25px;

  h2 {
    font-size: 1.5em;
    color: #34495e;
    border-bottom: 1px solid #eee;
    padding-bottom: 8px;
    margin-bottom: 15px;
  }

  table {
    width: 100%;
    border-collapse: collapse;

    th, td {
      padding: 10px;
      border-bottom: 1px solid #f0f0f0;
      text-align: left;
    }

    th {
      background-color: #f9f9f9;
      font-weight: 600;
      color: #555;
    }

    tbody tr:last-child td {
      border-bottom: none;
    }
    .text-right {
      text-align: right;
    }
  }
}

.invoice-summary {
  margin-top: 20px;
  padding-top: 15px;
  border-top: 2px solid #f0f0f0;
  display: flex;
  flex-direction: column;
  align-items: flex-end; // Aligner les totaux à droite

  .summary-item {
    display: flex;
    justify-content: space-between;
    width: 300px; // Ajustez la largeur
    padding: 6px 0;
    font-size: 1em;

    span:first-child {
      color: #555;
    }
    span:last-child {
      font-weight: bold;
    }

    &.total {
      font-size: 1.2em;
      font-weight: bold;
      color: #2c3e50;
      margin-top: 10px;
      padding-top: 10px;
      border-top: 1px dashed #ccc;
    }
  }
}

.invoice-notes {
  margin-top: 25px;
  padding-top: 15px;
  border-top: 1px solid #eee;

  h3 {
    font-size: 1.1em;
    color: #34495e;
    margin-bottom: 8px;
  }
  p {
    font-size: 0.9em;
    line-height: 1.6;
    color: #666;
  }
}

.loading-indicator, .error-message, .no-invoice {
  text-align: center;
  padding: 40px;
  font-size: 1.2em;
  color: #777;
}

.error-message {
  color: #e74c3c;
  border: 1px solid #f5c6cb;
  background-color: #f8d7da;
  padding: 15px;
  border-radius: 5px;
}

// Styles pour l'impression
@media print {
  body * {
    visibility: hidden;
  }
  .invoice-container, .invoice-container * {
    visibility: visible;
  }
  .invoice-container {
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    margin: 0;
    padding: 0;
    border: none;
    box-shadow: none;
    border-radius: 0;
  }
  .invoice-header .invoice-actions {
    display: none; // Cacher les boutons à l'impression
  }
  // Ajoutez d'autres ajustements de style pour l'impression si nécessaire
}
