commits.content:
// Re-declare or import your theme variables if not globally available
// For example, from your invoice-list.component.scss:
$primary-color: #41b3a3;
$warn-color: #f44336;
$text-color-primary: #333;
$text-color-secondary: #666;
$text-color-light: #fff;
$background-color: #f7f9fc;
$container-background: #fff;
$border-color: #e0e0e0;
$border-radius: 8px;
$card-shadow: 0 4px 12px rgba(0, 0, 0, 0.08);

.add-invoice-card {
  max-width: 900px; // Adjust as needed for content
  margin: 24px auto;
  background-color: $container-background;
  border-radius: $border-radius;
  box-shadow: $card-shadow;
  font-family: 'Poppins', sans-serif; // Consistent font

  mat-card-header {
    background-color: lighten($primary-color, 48%); // Very light primary for header
    padding: 16px 24px;
    border-bottom: 1px solid $border-color;
    mat-card-title {
      color: darken($primary-color, 10%);
      font-weight: 600;
      font-size: 1.25em;
    }
  }

  mat-card-content {
    padding: 24px;
  }

  .form-field {
    margin-bottom: 18px; // Spacing between form fields
  }

  .full-width {
    width: 100%;
  }

  .invoice-lines-section {
    margin-top: 28px;
    margin-bottom: 28px;
    padding: 20px;
    border: 1px solid $border-color;
    border-radius: $border-radius;
    background-color: lighten($background-color, 1.5%); // Slightly different background for section

    .section-title-bar {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 16px;
        padding-bottom: 12px;
        border-bottom: 1px solid lighten($border-color, 5%);
        h3 {
            font-size: 1.15em;
            font-weight: 600;
            color: $text-color-primary;
            margin: 0;
        }
    }
  }

  .invoice-line-item {
    display: flex;
    align-items: flex-start; // Better for multi-line descriptions
    gap: 16px; // Space between line item fields
    padding-bottom: 16px;
    margin-bottom: 16px;

    &:not(:last-child) {
      border-bottom: 1px dashed lighten($border-color, 7%);
    }

    .line-field {
      margin-bottom: 0; // Override default mat-form-field margin
    }

    .description-field {
      flex: 3 1 0%; // Takes more space, allows shrinking
    }
    .quantity-field {
      flex: 1 1 100px; // Base width 100px, can grow/shrink
      max-width: 120px;
    }
    .price-field {
      flex: 1 1 120px; // Base width 120px
      max-width: 150px;
    }

    .remove-line-button {
      margin-top: 8px; // Align with the top of the input fields
      color: $warn-color;
      flex-shrink: 0; // Prevent button from shrinking
    }
  }
  
  .form-array-error {
    font-size: 0.75em;
    margin-top: 4px;
    color: $warn-color;
  }

  .add-line-button {
    // Style for the button within .section-title-bar
    mat-icon {
      margin-right: 6px;
    }
  }

  .form-actions { // Styles for mat-card-actions
    padding: 16px 24px;
    background-color: lighten($background-color, 2.5%);
    border-top: 1px solid $border-color;
    border-bottom-left-radius: $border-radius; // Match card radius
    border-bottom-right-radius: $border-radius;

    .action-button {
      padding: 0 18px; // Slightly more padding
      min-height: 40px;
      font-size: 14px;
      font-weight: 500;
      margin-left: 10px; // Space between buttons

      mat-icon {
        margin-right: 8px;
        font-size: 18px;
      }
    }
    .save-button {
        background-color: $primary-color;
        color: $text-color-light;
         &:hover:not(:disabled) {
            background-color: darken($primary-color, 7%);
        }
    }
    .cancel-button {
        // Uses default mat-stroked-button styling which is usually fine
        // Or customize:
        // color: $text-color-secondary;
    }
  }

  // Responsive adjustments for line items
  @media (max-width: 768px) {
    .invoice-line-item {
      flex-wrap: wrap;

      .line-field {
        width: 100%;
        max-width: none;
        flex-basis: auto; // Reset flex-basis
      }
      .description-field { order: 1; }
      .quantity-field { order: 2; width: calc(50% - 8px); } // Two side-by-side
      .price-field { order: 3; width: calc(50% - 8px); }    // Two side-by-side
      .remove-line-button { order: 4; margin-left: auto; margin-top: 12px; }
    }
    .invoice-lines-section .section-title-bar {
        flex-direction: column;
        align-items: flex-start;
        gap: 10px;
        h3 { margin-bottom: 8px; }
    }
  }
  @media (max-width: 480px) {
    .invoice-line-item {
        .quantity-field, .price-field {
            width: 100%; // Stack quantity and price on very small screens
        }
    }
  }
}
