commits.content:
.project-details-container {
    width: 80%;
    margin: auto;
    padding: 20px;
    background: white;
    border-radius: 12px;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
}

.tabs {
    display: flex;
    border-bottom: 2px solid #ddd;
}

.tabs button {
    flex: 1;
    padding: 10px;
    background: none;
    border: none;
    cursor: pointer;
    font-size: 16px;
    font-weight: bold;
    transition: 0.3s;
}

.tabs button.active {
    border-bottom: 3px solid #2787C0;
    color: #2787C0;
}

.tab-content {
    padding: 20px;
}

.phase-list {
    list-style: none;
    padding: 0;
}

.phase-list li {
    padding: 10px;
    margin: 5px 0;
    background: #f9f9f9;
    border-radius: 5px;
    cursor: pointer;
    transition: 0.3s;
}

.phase-list li.selected {
    background: #2787C0;
    color: white;
}

.btn-add-phase {
    background: #e4be15;
    border: none;
    padding: 8px 15px;
    color: white;
    font-size: 14px;
    cursor: pointer;
    margin-bottom: 10px;
}
