+      color: white;
+      cursor: pointer;
+      display: flex;
+      align-items: center;
+    }
+    .bi {
+      margin-right: 5px;
+    }
+  }
+  
+  .add-project button {
+    background-color: #2787C0;
+    color: white;
+    border: none;
+    padding: 0.8rem;
+    cursor: pointer;
+  }
+  
+  .project-display {
+    .project-table {
+      width: 100%;
+      border-collapse: collapse;
+      th, td {
+        padding: 10px;
+        text-align: left;
+      }
+      th {
+        background-color: #f4f4f4;
+      }
+      td {
+        border-bottom: 1px solid #ddd;
+      }
+    }
+  
+    .project-cards {
+      display: flex;
+      flex-wrap: wrap;
+      gap: 20px;
+      .card {
+        width: calc(50% - 10px);
+        background-color: #f9f9f9;
+        padding: 20px;
+        border-radius: 8px;
+        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
+        .card-header {
+          font-size: 1.2rem;
+          margin-bottom: 10px;
+        }
+        .card-body {
+          .progress-bar {
+            height: 5px;
+            background-color: #2787C0;
+            margin-bottom: 10px;
+          }
+          .members img {
+            width: 30px;
+            height: 30px;
+            border-radius: 50%;
+            margin-right: 5px;
+          }
+        }
+      }
+    }
+  }
+  
