+}
+
+.input-container .icon {
+  position: absolute;
+  left: 10px;
+  font-size: 20px;
+  color: #ccc;
+}
+
+/* Date */
+.current-date {
+  font-size: 1.2rem;
+  background-color: #ccc;
+  border-radius: 30px;
+  padding: 10px;
+}
+
+/* Mode d'affichage et tri */
+.sort-and-view {
+  display: flex;
+  gap: 15px;
+  align-items: center;
+}
+
+.view-mode {
+  display: flex;
+  gap: 10px;
+}
+
+.view-mode button {
+  border-radius: 50%;
+  background-color: #E27D60;
+  border: none;
+  font-size: 1.2rem;
+  color: white;
+  cursor: pointer;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  width: 40px;
+  height: 40px;
+}
+
+.view-mode button.active {
+  background-color: #2787C0;
+}
+
+/* Bouton Ajouter Projet */
+.add-project {
+  cursor: pointer;
+  background: #E27D60;
+  border-radius: 40px;
+  font-weight: bold;
+  min-height: 3rem;
+  border: none;
+  color: white;
+  padding: 10px 20px;
+}
+
+.add-project i {
+  font-size: 0.8rem;
+}
+
+/* Contenu centré */
+.project-display.center-table {
+  display: flex;
+  justify-content: center;
+}
+
+.project-display.center-cards {
+  display: flex;
+  justify-content: center;
+}
+
+/* Tableau */
+.project-table {
+  width: 80%;
+  border-collapse: collapse;
+}
+
+.project-table th, .project-table td {
+  padding: 10px;
+  text-align: left;
+}
+
+.project-table th {
+  background-color: #f4f4f4;
+}
+
+.project-table tr.even {
+  background-color: #ffffff;
+}
+
+.project-table tr.odd {
+  background-color: #eaeaea;
+}
+
+.project-table td {
+  border-bottom: 1px solid #ddd;
+}
+
+/* Cartes */
+.project-cards {
+  display: flex;
+  flex-wrap: wrap;
+  gap: 20px;
+  justify-content: center;
+}
+
+.card {
+  width: 300px;
+  background-color: #f9f9f9;
+  padding: 20px;
+  border-radius: 8px;
+  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
+}
+
+.card-header {
+  font-size: 1.2rem;
+  margin-bottom: 10px;
+}
+
+.progress-bar {
+  height: 5px;
+  background-color: #2787C0;
+  margin-bottom: 10px;
+}
+
+.members img {
+  width: 30px;
+  height: 30px;
+  border-radius: 50%;
+  margin-right: 5px;
+}

