import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, catchError, forkJoin, map, mergeMap, of, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class U {
  private keycloakUrl = 'https://esmm.systeo.tn';
  private realm = 'systeodigital';
  private accessToken: string | null = null;

  constructor(private http: HttpClient) { }

  // Authentification pour obtenir le token
  login(clientId: string, username: string, password: string): Observable<any> {
    const body = new URLSearchParams();
    body.set('client_id', clientId);
    body.set('username', username);
    body.set('password', password);
    body.set('grant_type', 'password');

    const headers = new HttpHeaders({
      'Content-Type': 'application/x-www-form-urlencoded'
    });

    return this.http.post(
      `${this.keycloakUrl}/realms/${this.realm}/protocol/openid-connect/token`,
      body.toString(),
      { headers }
    ).pipe(
      map((response: any) => {
        this.accessToken = response.access_token;
        return response;
      }),
      catchError(error => {
        console.error('Login error:', error);
        return throwError(() => error);
      })
    );
  }

  // Créer un nouvel utilisateur
  createUser(userData: any): Observable<any> {
    if (!this.accessToken) {
      return throwError(() => new Error('Token non disponible. Veuillez vous authentifier d\'abord.'));
    }

    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${this.accessToken}`
    });

    const userPayload = {
      username: userData.email, // ou userData.name selon votre besoin
      email: userData.email,
      enabled: true,
      credentials: [{
        type: 'password',
        value: userData.password,
        temporary: false
      }]
    };

    return this.http.post(
      `${this.keycloakUrl}/admin/realms/${this.realm}/users`,
      userPayload,
      { headers }
    ).pipe(
      catchError(error => {
        console.error('Erreur création utilisateur:', error);
        return throwError(() => error);
      })
    );
  }

  // Récupérer tous les rôles disponibles
  getRoles(): Observable<any[]> {
    if (!this.accessToken) {
      return throwError(() => new Error('Token non disponible. Veuillez vous authentifier d\'abord.'));
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.accessToken}`
    });

    return this.http.get<any[]>(
      `${this.keycloakUrl}/admin/realms/${this.realm}/roles`,
      { headers }
    ).pipe(
      catchError(error => {
        console.error('Erreur récupération rôles:', error);
        return throwError(() => error);
      })
    );
  }

  // Assigner un rôle à un utilisateur
  assignRoleToUser(userId: string, roleName: string): Observable<any> {
    if (!this.accessToken) {
      return throwError(() => new Error('Token non disponible. Veuillez vous authentifier d\'abord.'));
    }

    return this.getRoles().pipe(
      mergeMap(roles => {
        const role = roles.find(r => r.name === roleName);
        if (!role) {
          return throwError(() => new Error(`Rôle ${roleName} non trouvé`));
        }

        const headers = new HttpHeaders({
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.accessToken}`
        });

        return this.http.post(
          `${this.keycloakUrl}/admin/realms/${this.realm}/users/${userId}/role-mappings/realm`,
          [role],
          { headers }
        );
      }),
      catchError(error => {
        console.error('Erreur assignation rôle:', error);
        return throwError(() => error);
      })
    );
  }

  // Trouver un utilisateur par email
  findUserByEmail(email: string): Observable<any> {
    if (!this.accessToken) {
      return throwError(() => new Error('Token non disponible. Veuillez vous authentifier d\'abord.'));
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.accessToken}`
    });

    return this.http.get<any[]>(
      `${this.keycloakUrl}/admin/realms/${this.realm}/users?email=${encodeURIComponent(email)}`,
      { headers }
    ).pipe(
      map(users => users.length > 0 ? users[0] : null),
      catchError(error => {
        console.error('Erreur recherche utilisateur:', error);
        return throwError(() => error);
      })
    );
  }

  // Méthode complète pour créer un utilisateur et lui assigner un rôle
  createUserWithRole(userData: any): Observable<any> {
    return this.createUser(userData).pipe(
      mergeMap(() => this.findUserByEmail(userData.email)),
      mergeMap(user => {
        if (!user || !user.id) {
          return throwError(() => new Error('Utilisateur créé mais non trouvé pour assignation de rôle'));
        }
        return this.assignRoleToUser(user.id, userData.role);
      }),
      catchError(error => {
        console.error('Erreur dans createUserWithRole:', error);
        return throwError(() => error);
      })
    );
  }
}
