// d:\satge pfe\application\ArchiManage\Front\WebFront\src\app\shared\project-access\project-access.component.scss

// Variables (Optional: Adjust colors to match your theme)
$primary-color: #0d6efd; // Bootstrap primary blue
$secondary-color: #6c757d; // Bootstrap secondary gray
$light-gray: #f8f9fa; // Bootstrap light gray
$border-color: #dee2e6; // Bootstrap border color

.modal-header {
  border-bottom: 1px solid $border-color;
  padding-bottom: 0.75rem;

  .modal-title {
    font-size: 1.1rem;
    font-weight: 500;
    display: flex; // Align icon and text
    align-items: center; // Align icon and text
    color: $secondary-color;

    strong {
      color: #333; // Darker color for the phase name
      margin-left: 0.5rem;
    }

    .bi-shield-lock {
      color: $primary-color; // Use primary color for the icon
      font-size: 1.3rem;
    }
  }
}

.project-access-body {
  padding: 1.5rem; // More padding in the body

  .position-relative {
    .search-icon {
      position: absolute;
      left: 10px;
      top: 50%;
      transform: translateY(-50%);
      color: $secondary-color;
      pointer-events: none; // Prevent icon from blocking input clicks
    }

    .search-input {
      padding-left: 30px; // Make space for the icon
      border-radius: 0.25rem; // Slightly rounded corners
    }
  }

  .loading-container,
  .user-list-container {
    margin-top: 1.5rem; // Space above the list/loader
    min-height: 150px; // Prevent layout jump when loading
    position: relative; // Needed for absolute positioning of loader if desired
  }

  .loading-container {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .user-list {
    max-height: 300px; // Limit height and allow scrolling if many users
    overflow-y: auto;
    border: 1px solid $border-color; // Add a border around the list
    border-radius: 0.25rem;
  }

  .user-item {
    padding: 0.75rem 1rem; // Adjust padding within list items
    border-bottom: 1px solid $border-color; // Separator line

    &:last-child {
      border-bottom: none; // No border for the last item
    }

    .user-info {
      flex-grow: 1; // Allow user info to take available space
      margin-right: 1rem; // Space between info and switch

      .user-name {
        font-weight: 500;
        color: #333;
      }
      .user-email {
        font-size: 0.85rem;
      }
    }

    .access-switch {
      // Adjust alignment if needed, Bootstrap's align-items-center on parent usually works well
      .form-check-label {
        cursor: pointer; // Indicate the label is clickable (though input is primary target)
      }
      .form-check-input {
         cursor: pointer;
         width: 3em; // Slightly larger switch
         height: 1.5em;
      }
    }

    // Optional: Hover effect
    &:hover {
      background-color: $light-gray;
    }
  }

  .no-users-message {
    border: 1px dashed $border-color;
    border-radius: 0.25rem;
    background-color: $light-gray;
    margin-top: 1.5rem;

    .bi-people {
      color: $secondary-color;
    }
  }
}

.modal-footer {
  border-top: 1px solid $border-color;
  padding-top: 1rem;
  background-color: $light-gray; // Subtle background for footer

  .btn-primary {
    min-width: 180px; // Ensure button doesn't resize too much when loading text changes
  }
  .btn-outline-secondary {
     min-width: 100px;
  }
}
