+// En-tête de la modale
+.modal-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 16px 24px;
+  border-bottom: 1px solid $border-color;
+
+  h2 {
+    font-size: 1.25em;
+    font-weight: 600;
+    color: $primary-color;
+    margin: 0;
+    flex-grow: 1; // Prend l'espace
+  }
+
+  .close-button {
+    background: none;
+    border: none;
+    cursor: pointer;
+    padding: 8px; // Zone cliquable
+    margin-left: 16px;
+    line-height: 1; // Empêche hauteur excessive
+    color: $text-color-secondary;
+    border-radius: 50%;
+    transition: background-color 0.2s ease;
+
+    &:hover {
+      background-color: rgba(0, 0, 0, 0.05);
+    }
+
+    i { // Style pour l'icône
+      font-size: 24px; // Taille de l'icône close
+      display: block; // Assure un bon alignement
+    }
+  }
+}
+
+// Contenu de la modale
+.modal-content {
+  padding: 20px 24px;
+  overflow-y: auto; // Permet le défilement si le contenu est trop grand
+  flex-grow: 1; // Prend l'espace vertical disponible
+  position: relative; // Pour le loading indicator
+
+  .loading-indicator {
+    position: absolute;
+    top: 0;
+    left: 0;
+    right: 0;
+    z-index: 10;
+    // Style pour le placeholder de la barre de progression
+    .progress-bar-placeholder {
+      height: 4px;
+      background-color: $primary-color;
