+      // Ajoutez une animation simple si vous n'avez pas de composant dédié
+    }
+  }
+
+  h3 {
+    font-size: 1.1em;
+    margin-top: 0; // Retirer marge sup si premier élément
+    margin-bottom: 16px;
+    color: $text-color-primary;
+    font-weight: 500;
+  }
+
+  // Section Liste des membres
+  .members-list-section {
+    margin-bottom: 24px;
+
+    .table-container {
+      max-height: 300px;
+      overflow-y: auto;
+      border: 1px solid $border-color;
+      border-radius: 8px / 2;
+      background-color: $container-background;
+      box-shadow: $card-shadow; // Ajoute une légère ombre comme mat-elevation-z2
+    }
+
+    .members-table {
+      width: 100%;
+      border-collapse: collapse; // Important pour les bordures
+
+      th, td {
+        padding: 10px 16px;
+        text-align: left;
+        vertical-align: middle;
+        border-bottom: 1px solid $border-color;
+      }
+
+      th {
+        font-weight: 600;
