+    }
+  }
+
+  // Séparateur
+  hr {
+    border: none;
+    border-top: 1px solid $border-color;
+    margin: 24px 0;
+  }
+
+  // Section Ajouter un membre
+  .add-member-section {
+    .add-member-form {
+      display: flex;
