// Utiliser les variables globales si possible via @use ou @import
// Exemple avec des variables locales pour la démo
$primary-color: #41B3A3;
$warn-color: #F44336;
$text-color-primary: #333;
$text-color-secondary: #666;
$border-color: #e0e0e0;
$background-color-hover: #f5f5f5;
$container-background: #fff;
$border-radius: 8px;
$avatar-size: 32px;

:host {
  display: block; // Nécessaire pour que la largeur/hauteur fonctionne bien
}

// Style du titre
h2[mat-dialog-title] {
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid $border-color;
  margin-bottom: 0; // Annule la marge par défaut
  padding: 16px 24px;
  font-size: 1.25em;
  font-weight: 600;
  color: $primary-color;

  .close-button {
    // Positionne le bouton close
    margin-left: 16px; // Espace par rapport au titre
  }
}

// Style du contenu
mat-dialog-content {
  padding: 20px 24px; // Espacement intérieur
  position: relative; // Pour le positionnement de l'indicateur de chargement

  .loading-indicator {
    position: absolute; // Ou sticky si vous préférez qu'il reste en haut
    top: 0;
    left: 0;
    right: 0;
    z-index: 10; // Au-dessus du contenu
  }

  h3 {
    font-size: 1.1em;
    margin-bottom: 16px;
    color: $text-color-primary;
    font-weight: 500;
  }

  // Section Liste des membres
  .members-list-section {
    margin-bottom: 24px;

    .table-container {
      max-height: 300px; // Limite la hauteur si beaucoup de membres
      overflow-y: auto;
      border: 1px solid $border-color;
      border-radius: $border-radius / 2;
      background-color: $container-background; // Fond blanc pour la table
    }

    .members-table {
      width: 100%;

      th.mat-header-cell, td.mat-cell {
        padding: 10px 16px;
        vertical-align: middle;
        border-bottom-color: $border-color; // Couleur de la bordure de ligne
      }

      th.mat-header-cell {
        font-weight: 600;
        font-size: 0.85em;
        color: $text-color-secondary;
        text-transform: uppercase;
      }

      td.mat-cell {
        font-size: 0.95em;
        color: $text-color-primary;
      }

      .member-avatar {
        width: $avatar-size;
        height: $avatar-size;
        border-radius: 50%;
        margin-right: 12px;
        vertical-align: middle;
        object-fit: cover;
        border: 1px solid darken($border-color, 5%);
      }

      .role-badge {
        background-color: rgba($primary-color, 0.15);
        color: darken($primary-color, 10%);
        padding: 3px 8px;
        border-radius: 12px; // Badge arrondi
        font-size: 0.8em;
        font-weight: 500;
        display: inline-block; // Pour que le padding fonctionne bien
      }

      button[mat-icon-button] {
        width: 36px;
        height: 36px;
        line-height: 36px;
        mat-icon { font-size: 20px; }
      }
    }

    .no-members-message {
        padding: 20px;
        text-align: center;
        color: $text-color-secondary;
        font-style: italic;
    }
  }

  mat-divider {
    margin: 24px 0; // Espace autour du séparateur
  }

  // Section Ajouter un membre
  .add-member-section {
    .add-member-form {
      display: flex;
      align-items: flex-start; // Aligne le haut du select et du bouton
      gap: 16px; // Espace entre le select et le bouton
      flex-wrap: wrap; // Permet au bouton de passer en dessous sur petit écran

      .user-select-field {
        flex-grow: 1; // Prend l'espace disponible
        min-width: 250px; // Largeur minimale pour le select
      }

      button[mat-flat-button] {
        // Ajustement pour aligner avec le champ outline
        margin-top: 8px; // Peut nécessiter ajustement selon la hauteur exacte
        height: 52px; // Hauteur approx d'un champ outline standard
        mat-icon { margin-right: 8px; }
      }
    }
    .info-text {
        font-size: 0.85em;
        color: $text-color-secondary;
        margin-top: 12px;
        font-style: italic;
    }
  }
}

// Style des actions (bas de la modale)
mat-dialog-actions {
  padding: 12px 24px;
  border-top: 1px solid $border-color;
  background-color: lighten($background-color-hover, 2%); // Léger fond pour les actions
}

// Style pour les snackbars d'erreur (optionnel)
.error-snackbar {
  background-color: $warn-color;
  color: white;
}
