+
+      p {
+        margin: 0;
+        font-weight: 500;
+      }
+    }
+  }
+
+  .progress-container {
+    display: flex;
+    align-items: center;
+    gap: 15px;
+
+    mat-progress-bar {
+      flex-grow: 1;
+      height: 8px;
+      border-radius: 4px;
+    }
+
+    .progress-value {
+      font-weight: 600;
+      color: $primary-color;
+    }
+  }
+
+  .subtasks-list {
+    .subtask-item {
+      display: flex;
+      justify-content: space-between;
+      align-items: center;
+      padding: 8px 0;
+      border-bottom: 1px solid rgba($border-color, 0.5);
+
+      .subtask-info {
+        display: flex;
+        align-items: center;
+        gap: 8px;
+
+        i {
+          color: $success-color;
+        }
+      }
+
+      .assignee {
+        background: rgba($primary-color, 0.1);
+        padding: 4px 10px;
+        border-radius: 12px;
+        font-size: 0.8rem;
+
+        .assignee-name {
+          color: $primary-color;
+          font-weight: 500;
+        }
+      }
+    }
+  }
+
+  .attachments-list {
+    .attachment-item {
+      display: flex;
+      align-items: center;
+      gap: 10px;
+      padding: 10px 0;
+      border-bottom: 1px solid rgba($border-color, 0.5);
+
+      i {
+        color: $accent-color;
+        font-size: 1.2rem;
+      }
+
+      .attachment-info {
+        flex-grow: 1;
+
+        strong {
+          display: block;
+          overflow-wrap: break-word;
+          font-size: 0.9rem;
+        }
+
+        small {
+          color: $text-color-secondary;
+          font-size: 0.75rem;
+        }
+      }
+
+      button {
+        color: $primary-color;
+      }
+    }
+  }
+
+  .task-description {
+    line-height: 1.6;
+    color: $text-color-primary;
+  }
+
+  .requirements-list, .guidelines-list {
+    list-style: none;
+    padding: 0;
+    margin: 0;
+
+    li {
+      display: flex;
+      align-items: flex-start;
+      gap: 10px;
+      padding: 8px 0;
+      border-bottom: 1px solid rgba($border-color, 0.3);
+
+      i {
+        color: $success-color;
+        margin-top: 2px;
+      }
+    }
+  }
+
+  .guidelines-list li i {
+    color: $accent-color;
+  }
+
+  .activity-timeline {
+    .activity-item {
+      padding: 15px 0;
+      border-bottom: 1px solid rgba($border-color, 0.3);
+
+      .activity-header {
+        display: flex;
+        justify-content: space-between;
+        margin-bottom: 5px;
+
+        .user {
+          font-weight: 600;
+          color: $primary-color;
+        }
+
+        .timestamp {
+          color: $text-color-secondary;
+          font-size: 0.8rem;
+        }
+      }
+
+      .activity-content {
+        margin: 0;
+        font-size: 0.9rem;
+        line-height: 1.5;
+      }
+
+      .reply-btn {
+        background: none;
+        border: none;
+        color: $primary-color;
+        font-size: 0.8rem;
+        margin-top: 5px;
+        cursor: pointer;
+        padding: 0;
+
+        &:hover {
+          text-decoration: underline;
+        }
+      }
+    }
+  }
+
+  .new-comment {
+    margin-top: 20px;
+
+    textarea {
+      width: 100%;
+      padding: 10px;
+      border: 1px solid $border-color;
+      border-radius: 6px;
+      min-height: 80px;
+      resize: vertical;
+      margin-bottom: 10px;
+    }
+
+    button {
+      float: right;
+    }
+  }
+}
+
+@media (max-width: 768px) {
+  .task-details-container {
+    width: 95vw;
+    padding: 15px;
+  }
+
+  .task-content {
+    flex-direction: column !important;
+  }
+
+  .date-range {
+    flex-direction: column;
+    gap: 10px !important;
+  }
+}
