+$text-color-secondary: #666;
+$text-color-light: #fff;
+
+$background-color: #f7f9fc;
+$container-background: #fff;
+$border-color: #e0e0e0;
+$border-radius: 8px;
+$card-shadow: 0 4px 12px rgba(0, 0, 0, 0.08);
+
+$priority-high-color: #D9534F; // Adjusted warn color
+$priority-medium-color: #E9A28A; // Adjusted accent color
+$priority-low-color: #6CBBAF;
+$priority-high-bg: lighten($priority-high-color, 40%); // Corrected lighten syntax if needed elsewhere
+$priority-medium-bg: lighten($priority-medium-color, 35%); // Corrected lighten syntax if needed elsewhere
+$priority-low-bg: lighten($priority-low-color, 40%);
+
+
+.task-details-container {
+  display: flex; // Rendre le conteneur principal flex
+  flex-direction: column;
+  background: white;
+  border-radius: 12px;
+  padding: 24px;
+  
+
+  max-height: 90vh;
+  //overflow-y: auto;
+  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
+
+  .task-header {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    margin-bottom: 20px;
+    padding-bottom: 15px;
+    border-bottom: 1px solid $border-color;
+
+    h2 {
+      margin: 0;
+      color: $primary-color;
+      font-size: 1.8rem;
+      overflow-wrap: break-word;
+    }
+
+    .close-btn {
+      color: $text-color-secondary;
+      transition: all 0.2s;
+
+      &:hover {
+        color: $warn-color;
+        transform: scale(1.1);
+      }
+    }
+  }
+
+  .task-content {
+    display: flex;
+    flex: 1; // Permet à cette zone de s'étendre pour remplir l'espace vertical restant
+    //overflow-y: hidden;
+    gap: 30px;
+    min-height: 0;
+    .task-left-column {
+      flex: 1;
+      min-width: 300px;
+      overflow-y: auto; 
+      overflow-x: hidden;
+      min-height: 0;
+    }
+
+    .task-right-column {
+      flex: 2;
+      overflow-y: auto; // Allow this column to scroll if needed
+      min-height: 0;
+      overflow-x: hidden; 
+    }
+  }
+
+  .task-section {
+    margin-bottom: 25px;
+
+    h3 {
+      color: $primary-color;
+      margin-top: 0;
+      margin-bottom: 15px;
+      font-size: 1.2rem;
+      display: flex;
+      align-items: center;
+      gap: 8px;
+
+      &::before {
+        content: '';
+        display: block;
+        width: 8px;
+        height: 8px;
+        background: $primary-color;
+        border-radius: 50%;
+      }
+    }
+  }
+
+  .priority-badge {
+    display: inline-flex;
+    align-items: center;
+    padding: 6px 12px;
+    border-radius: 20px;
+    font-weight: 600;
+    font-size: 0.85rem;
+    margin-bottom: 15px;
+
+    &.high {
+      background-color: rgba($priority-high-color, 0.1);
+      color: $priority-high-color;
+      border: 1px solid rgba($priority-high-color, 0.3);
+    }
+
+    &.medium {
+      background-color: rgba($priority-medium-color, 0.1);
+      color: $priority-medium-color;
+      border: 1px solid rgba($priority-medium-color, 0.3);
+    }
+
+    &.low {
+      background-color: rgba($priority-low-color, 0.1);
+      color: $priority-low-color;
+      border: 1px solid rgba($priority-low-color, 0.3);
+    }
+  }
+
+  .status-badge {
+    display: inline-flex;
+    align-items: center;
+    padding: 6px 12px;
+    border-radius: 20px;
+    font-weight: 600;
+    font-size: 0.85rem;
+    margin-bottom: 15px;
+    margin-left: 10px;
+    &.TODO {
+      background-color: rgba($accent-color, 0.3);
+      color: $accent-color;
+      border: 1px solid rgba($accent-color, 0.6);
+    
+    }
+
+    &.COMPLETED {
+      background-color: rgba($success-color, 0.3);
+      color: $success-color;
+      border: 1px solid rgba($success-color, 0.6);
+  
+    }
+
+    &.IN_PROGRESS {
+      background-color: rgba($primary-color, 0.3);
+      color: $primary-color;
+      border: 1px solid rgba($primary-color, 0.6);
+ 
+    }
+  }
+
+  .date-range {
+    display: flex;
+    gap: 20px;
+
+    .date-item {
+      label {
+        display: block;
+        font-size: 0.8rem;
+        color: $text-color-secondary;
+        margin-bottom: 5px;
+      }
+
+      p {
+        margin: 0;
+        font-weight: 500;
+      }
+    }
+  }
+
+  .progress-container {
+    display: flex;
+    align-items: center;
+    gap: 15px;
+
+    mat-progress-bar {
+      flex-grow: 1;
+      height: 8px;
+      border-radius: 4px;
+    }
+
+    .progress-value {
+      font-weight: 600;
+      color: $primary-color;
+    }
+  }
+
+  .subtasks-list.detailed-subtasks {
+    
+    .subtask-item {
+       display: flex;
+      flex-direction: column;
+      padding: 12px 0;
+      border-bottom: 1px solid rgba($border-color, 0.5);
+
+      gap: 8px;
+
+     transition: background-color 0.2s ease-in-out;
+
+      &:last-child {
+        border-bottom: none;
+      }
+
+      &.editing {
+        background-color: lighten($background-color, 3%); // Subtle highlight for editing row
+        border-radius: $border-radius / 2;
+        padding-left: 8px;
+        padding-right: 8px;
+      }
+
+      .subtask-info-display, .subtask-info-edit {
+        display: flex;
+        align-items: center;
+        justify-content: space-between;
+        gap: 10px; // Spacing between elements in a row
+        width: 100%;
+      }
+
+      .subtask-info-display {
+        .subtask-name {
+          flex: 2 1 0%;
+          font-weight: 500;
+        white-space: nowrap;
+          overflow: hidden;
+          text-overflow: ellipsis;
+          max-width: 200px; // Adjust as needed
+        }
+        .subtask-date {
+          flex: 1 1 auto;
+          font-size: 0.8rem;
+          color: $text-color-secondary;
+        text-align: center;
+          min-width: 130px; // Prevents date from wrapping too easily
+        }
+        .subtask-priority, .subtask-status {
+          flex: 0 0 auto; // Don't grow or shrink, take content width
+          padding: 4px 10px;
+          border-radius: 16px;
+          font-size: 0.75rem;
+          font-weight: 600;
+          text-align: center;
+          min-width: 90px; // Minimum width for badges
+          text-transform: capitalize;
+
+          // Priority classes
+          &.high { background-color: $priority-high-bg; color: $priority-high-color; border: 1px solid $priority-high-color;}
+          &.medium { background-color: $priority-medium-bg; color: $priority-medium-color; border: 1px solid $priority-medium-color;}
+          &.low { background-color: $priority-low-bg; color: $priority-low-color; border: 1px solid $priority-low-color;}
+
+          // Status classes
+          &.todo { background-color: rgba($accent-color, 0.15); color: $accent-color; border: 1px solid $accent-color;}
+          &.in-progress { background-color: rgba($primary-color, 0.15); color: $primary-color; border: 1px solid $primary-color;}
+          &.completed { background-color: rgba($success-color, 0.15); color: $success-color; border: 1px solid $success-color;}
+        }
+      }
+
+      .subtask-info-edit {
+        gap: 8px; // Slightly smaller gap for edit fields
+        .subtask-edit-name {
+          flex: 2 1 180px; // More space for name input
+        }
+        .subtask-edit-dates {
+          flex: 1 1 220px; // Combined width for two date inputs and separator
+          display: flex;
+          align-items: center;
+          gap: 4px;
+          input[type="date"] {
+            flex: 1;
+            min-width: 100px; // Ensure date input is usable
+          }
+        }
+        .subtask-edit-selects {
+          flex: 1 1 200px; // Combined width for two selects
+          display: flex;
+          gap: 8px;
+          select {
+            flex: 1;
+            min-width: 90px;
+          }
+        }
+
+        // Common style for form controls in edit mode
+        .form-control-sm, .form-select-sm {
+          font-size: 0.8rem; // Smaller font for compact inputs
+          padding: 0.25rem 0.5rem;
+          border: 1px solid $border-color;
+          border-radius: 4px;
+          background-color: #fff;
+          &:focus {
+            border-color: $primary-color;
+            box-shadow: 0 0 0 0.15rem rgba($primary-color, 0.2);
+          }
+        }
+      }
+
+      .subtask-actions, .subtask-actions-edit {
+        flex: 0 0 auto; // Actions take minimal space
+        display: flex;
+        align-items: center;
+        gap: 4px;
+        // margin-left: auto; // Pushes actions to the right if not enough items to fill space
+
+        button[mat-icon-button] {
+          width: 30px; // Smaller buttons for subtask actions
+          height: 30px;
+          i.bi {
+            font-size: 14px; // Smaller icons
+          }
+        }
+      }
+    }
+    .no-comments { // Re-using .no-comments style for "No subtasks"
+        padding: 15px 0;
+        color: $text-color-secondary;
+        font-style: italic;
+        text-align: center;
+    }
+  }
+
+  .attachments-list {
+    .attachment-item {
+      display: flex;
+      align-items: center;
+      gap: 10px;
+      padding: 10px 0;
+      border-bottom: 1px solid rgba($border-color, 0.5);
+
+      i {
+        color: $accent-color;
+        font-size: 1.2rem;
+      }
+
+      .attachment-info {
+        flex-grow: 1;
+
+        strong {
+          display: block;
+          overflow-wrap: break-word;
+          font-size: 0.9rem;
+        }
+
+        small {
+          color: $text-color-secondary;
+          font-size: 0.75rem;
+        }
+      }
+
+      button {
+        color: $primary-color;
+      }
+    }
+  }
+
+  .task-description {
+    line-height: 1.6;
+    color: $text-color-primary;
+    overflow-wrap: break-word;
+  }
+
+  .requirements-list, .guidelines-list {
+    list-style: none;
+    padding: 0;
+    margin: 0;
+
+    li {
+      display: flex;
+      align-items: flex-start;
+      gap: 10px;
+      padding: 8px 0;
+      border-bottom: 1px solid rgba($border-color, 0.3);
+position: relative; // For positioning actions absolutely if needed, or use flex for actions
+    gap: 10px; // Gap between avatar, body, and actions
+ 
+      i {
+        color: $success-color;
+        margin-top: 2px;
+      }
+      span {
+        flex: 1; // Allow text to take space and wrap
+        overflow-wrap: break-word; // Ajouter pour les requirements/guidelines
+      }
+    }
+  }
+
+  .guidelines-list li i {
+    color: $accent-color;
+  }
+
+  .activity-timeline {
+    .activity-item {
+      display: flex;
+      align-items: flex-start; // Aligns icon with the start of the text block
+      padding: 12px 0;
+      border-bottom: 1px solid rgba($border-color, 0.3);
+
+      &:last-child {
+        border-bottom: none;
+      }
+
+      .activity-icon {
+        margin-right: 12px;
+        color: $text-color-secondary; // Default icon color
+        font-size: 1.1rem; // Icon size
+        width: 24px; // Fixed width for alignment
+        text-align: center;
+        flex-shrink: 0;
+        margin-top: 2px; // Small adjustment to align with the first line of text
+
+        &.comment-icon { color: $primary-color; }
+        &.update-icon { color: $accent-color; }
+        &.delete-icon { color: $warn-color; }
+        &.create-icon { color: $success-color; } // Assuming you have $success-color
+      }
+
+      .activity-content-wrapper {
+        flex-grow: 1;
+        display: flex;
+        flex-direction: column;
+      }
+
+      .activity-main-info {
+        margin-bottom: 4px;
+
+        .user {
+          font-weight: 600;
+          color: $primary-color;
+       margin-right: 5px;
+        }
+        .action-description {
+          color: $text-color-primary;
+          font-size: 0.9rem;
+          line-height: 1.4;
+          overflow-wrap: break-word; // Ensure long descriptions wrap
+        }
+      }
+
+      .activity-meta {
+
+        .timestamp {
+          color: $text-color-secondary;
+          font-size: 0.8rem;
+        }
+      }
+
+       
+    }
+  }
+// Placeholder selectors for shared styles
+  %comment-form-textarea-base {
+    width: 100%;
+    padding: 10px;
+    border: 1px solid $border-color;
+    border-radius: 6px;
+    min-height: 80px;
+    resize: vertical;
+    margin-bottom: 10px;
+  }
+
+  %comment-form-button-base {
+    
+    // Add any other shared button styles here, e.g., from mat-raised-button if applicable
+  }
+
+  .new-comment {
+    margin-top: 20px;
+
+    textarea {
+     @extend %comment-form-textarea-base;
+      width: 100%; 
+ 
+    }
+
+    button {
+      @extend %comment-form-button-base;
+  
+        // Les styles de mat-raised-button sont appliqués via HTML
+    }
+    // Utiliser flexbox pour aligner le bouton à droite
+    display: flex;
+    flex-direction: column;
+    align-items: flex-end;
+  }
+}
+.avatar-circle {
+  width: 32px; // Standard avatar size
+  height: 32px;
+  border-radius: 50%;
+  background-color: $primary-color; // Default background, can be dynamic
+  color: $text-color-light;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  font-weight: 600;
+  font-size: 0.9rem; // Adjust for letter size
+  text-transform: uppercase;
+  margin-right: 10px;
+  flex-shrink: 0; // Prevent shrinking if comment text is long
+}
+// Styles for Comments Section (similar to Activity Timeline)
+.comments-list {
+  .comment-item {
+     display: flex; // For aligning avatar and comment body
+    align-items: flex-start;
+    padding: 15px 0;
+    border-bottom: 1px solid rgba($border-color, 0.3);
+
+    &:last-child {
+      border-bottom: none;
+    }
+.comment-body { // Wrapper for header and content, allows flex-grow
+      flex-grow: 1;
+    }
+    .comment-header {
+      display: flex;
+      justify-content: space-between;
+      margin-bottom: 5px;
+
+      .user {
+        font-weight: 600;
+        color: $primary-color; // Or a different color for comment users
+      }
+
+      .timestamp {
+        color: $text-color-secondary;
+        font-size: 0.8rem;
+      }
+    }
+
+    .comment-content {
+      margin: 0;
+      font-size: 0.9rem;
+      overflow-wrap: break-word;
+      line-height: 1.5;
+      color: $text-color-primary;
+    }
+    
+  }
+  .edit-comment-area {
+    width: 100%;
+    .edit-comment-textarea {
+      @extend %comment-form-textarea-base; // Reuse existing textarea styles
+      min-height: 60px; // Adjust as needed
+      margin-bottom: 8px;
+    }
+    .edit-comment-actions {
+      display: flex;
+      justify-content: flex-end;
+      gap: 8px;
+      margin-top: 12px; // Un peu plus d'espace au-dessus des boutons
+
+      button.mat-raised-button,
+      button.mat-button {
+        padding: 0 12px; // Ajuster le padding horizontal
+        min-width: 70px; // Donner une largeur minimale pour une meilleure apparence
+        line-height: 32px; // Ajuster la hauteur de ligne pour des boutons plus petits
+        font-size: 0.85rem;}
+    }
+  }
+
+  .comment-actions {
+    display: flex;
+    gap: 4px;
+    margin-left: auto; // Pushes actions to the right
+    // align-self: flex-start; // Aligns actions to the top of the comment item
+    // For absolute positioning:
+    // position: absolute;
+    // top: 10px;
+    // right: 10px;
+  }
+  .no-comments { // Style for when there are no comments
+    padding: 15px 0;
+    color: $text-color-secondary;
+    font-style: italic;
+    text-align: center;
+  }
+}
