import { Component } from '@angular/core';

@Component({
  selector: 'app-task-details',
  templateUrl: './task-details.component.html',
  styleUrl: './task-details.component.scss'
})
export class TaskDetailsComponent {
  @Input() task: any;
  @Input() phaseName: string = '';
  
  comments: Comment[] = [];
  mediaFiles: MediaFile[] = [];
  taskHistory: TaskHistory[] = [];
  newComment: string = '';

  constructor(
    public activeModal: NgbActiveModal,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.loadTaskDetails();
  }

  loadTaskDetails(): void {
    // Charger les commentaires
    this.taskService.getComments(this.task._id).subscribe(comments => {
      this.comments = comments;
    });

    // Charger les médias
    this.taskService.getMediaFiles(this.task._id).subscribe(media => {
      this.mediaFiles = media;
    });

    // Charger l'historique
    this.taskService.getTaskHistory(this.task._id).subscribe(history => {
      this.taskHistory = history;
    });
  }

  addComment(): void {
    if (this.newComment.trim()) {
      const comment: Comment = {
        text: this.newComment,
        user: { name: 'Utilisateur actuel', avatar: 'assets/images/user.png' },
        date: new Date()
      };

      this.taskService.addComment(this.task._id, comment).subscribe(() => {
        this.comments.push(comment);
        this.newComment = '';
      });
    }
  }

  handleFileInput(event: any): void {
    const files = event.target.files;
    for (let file of files) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        const media: MediaFile = {
          url: e.target.result,
          type: file.type.includes('image') ? 'image' : 'video',
          name: file.name
        };
        this.taskService.addMedia(this.task._id, media).subscribe(() => {
          this.mediaFiles.push(media);
        });
      };
      reader.readAsDataURL(file);
    }
  }

  deleteMedia(media: MediaFile): void {
    this.taskService.deleteMedia(this.task._id, media).subscribe(() => {
      this.mediaFiles = this.mediaFiles.filter(m => m.url !== media.url);
    });
  }

  updateStatus(): void {
    this.taskService.updateTaskStatus(this.task._id, this.task.status).subscribe(() => {
      this.addHistoryEntry(`Statut changé à ${this.task.status}`);
    });
  }

  updatePriority(): void {
    this.taskService.updateTaskPriority(this.task._id, this.task.priority).subscribe(() => {
      this.addHistoryEntry(`Priorité changée à ${this.task.priority}`);
    });
  }

  addHistoryEntry(action: string): void {
    const historyEntry: TaskHistory = {
      user: { name: 'Utilisateur actuel' },
      action: action,
      date: new Date()
    };
    this.taskHistory.unshift(historyEntry);
  }

  saveChanges(): void {
    this.taskService.updateTask(this.task._id, this.task).subscribe(() => {
      this.activeModal.close(this.task);
    });
  }
}
