+  phaseId: string | null = null;
+  phases: any[] = []; 
+  todo: any[] = [];
+  inProgress: any[] = [];
+  done: any[] = [];
+
+  constructor(private router: Router, private route: ActivatedRoute) {}
+
+  ngOnInit(): void {
+    const phaseId = this.route.snapshot.paramMap.get('_id'); 
+
+      if (phaseId) {
+        this.loadPhase(phaseId);
+      } else {
+        this.goBack();
+      }
+    
+    this.initializeTasks();
+    this.loadAllPhases();
+  }
+
+
+  private loadPhase(phaseId: string ) {
+    if (!phaseId) {
+      console.error('No phase ID provided');
+      this.goBack();
+      return;
+    }
+  
+    const mockPhases = [
+      {
+        _id: "67c85299bae88e131703dd8e",
+        name: "Étude de Faisabilité",
+        tasks: [
+          { _id: "1", name: "Analyse technique", status: "PENDING" },
+          { _id: "2", name: "Étude réglementaire", status: "PENDING" }
+        ]
+      },
+      {
+        _id: "67c852e7bae88e131703dd92",
+        name: "Conception Détaillée",
+        tasks: []
+      }
+    ];
+    
+    
+    this.phase = mockPhases.find((p:any) => p._id === +phaseId);
+    if (this.phase) {
+      this.initializeTasks();
+    } else {
+      console.error('Phase not found');
+      this.goBack();
+    }
+  }
+
+  private loadAllPhases() {
+    // Simulation - remplacez par un appel à votre service
+    this.phases = [
+      {
+        _id: "67c85299bae88e131703dd8e",
+        name: "Étude de Faisabilité",
+        taskCount: 2
+      },
+      {
+        _id: "67c852e7bae88e131703dd92",
+        name: "Conception Détaillée",
+        taskCount: 0
+      }
+    ];
+  }
+
+  
+  private initializeTasks() {
+    if (!this.phase?.tasks) return;
+
+    this.todo = this.phase.tasks
+      .filter((task: any) => task.status === 'PENDING')
+      .map((task: any) => ({
+        id: task._id,
+        name: task.name,
+        status: 'todo'
+      }));
+
+    this.inProgress = this.phase.tasks
+      .filter((task: any) => task.status === 'IN_PROGRESS')
+      .map((task: any) => ({
+        id: task._id,
+        name: task.name,
+        status: 'inProgress'
+      }));
+
+    this.done = this.phase.tasks
+      .filter((task: any) => task.status === 'COMPLETED')
+      .map((task: any) => ({
+        id: task._id,
+        name: task.name,
+        status: 'done'
+      }));
+  }
+  switchPhase(newPhase: any) {
+    if (newPhase.id !== this.phase?.id) {
+      // Naviguer vers la nouvelle phase
+      this.router.navigate(['phase', newPhase.id], { 
+        relativeTo: this.route,
+        state: { phase: newPhase }
+      }).then(() => {
+        this.phase = newPhase;
+        this.initializeTasks();
+      });
+    }
+  }
+  drop(event: CdkDragDrop<any[]>) {
+    if (event.previousContainer === event.container) {
