import { Component } from '@angular/core';

@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrl: './tasks.component.scss'
})
export class TasksComponent {
  @Input() phase: any;
  
  todo: any[] = [];
  inProgress: any[] = [];
  done: any[] = [];

  constructor(private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    if (this.phase) {
      // Initialiser les tâches (c'est un exemple, adaptez selon votre structure)
      this.todo = this.phase.tasks.map((task: string) => ({
        id: Math.random().toString(36).substring(2),
        name: task,
        status: 'todo'
      }));
    }
  }

  drop(event: CdkDragDrop<any[]>) {
    if (event.previousContainer === event.container) {
      moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
    } else {
      transferArrayItem(
        event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex
      );
      // Mettre à jour le statut des tâches
      this.updateTaskStatus();
    }
  }

  updateTaskStatus() {
    this.todo.forEach(task => task.status = 'todo');
    this.inProgress.forEach(task => task.status = 'inProgress');
    this.done.forEach(task => task.status = 'done');
  }

  goBack() {
    this.router.navigate(['../'], { relativeTo: this.route });
  }

  addTask() {
    const taskName = prompt('Entrez le nom de la nouvelle tâche:');
    if (taskName) {
      this.todo.push({
        id: Math.random().toString(36).substring(2),
        name: taskName,
        status: 'todo'
      });
    }
  }
}
