commits.content:
.sidebar {
    width: 250px;
    background-color: #2c3e50;
    height: 100vh;
    padding: 20px;
    color: white;
    position: fixed;
  }
  
  .logo {
    text-align: center;
    margin-bottom: 20px;
  }
  
  .logo img {
    width: 100px;
    height: auto;
  }
  
  .menu {
    list-style: none;
    padding: 0;
  }
  
  .menu li {
    margin: 15px 0;
  }
  
  .menu li a {
    color: white;
    text-decoration: none;
    font-size: 18px;
    display: block;
    padding: 10px;
    border-radius: 5px;
    transition: background-color 0.3s ease;
  }
  
  .menu li a:hover,
  .menu li a.active {
    background-color: #3498db;
  }
  
  .menu li a.active {
    font-weight: bold;
  }
