commits.content:
$primary-color: #3D5653; // Vert foncé
$secondary-color: #E27D60; // Rose saumoné
$text-color: #F4F4F4; // Texte clair
$bg-color: #2C3E50; // Fond d’accentuation
$hover-color: #C75C50; // Hover bouton

.register-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: radial-gradient(circle at top left, $secondary-color, $bg-color);
}

.register-card {
  display: flex;
  background: $primary-color;
  border-radius: 15px;
  padding: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  max-width: 800px;
  width: 90%;
  color: $text-color;

  .illustration {
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;

    img {
      max-width: 100%;
      height: auto;
    }
  }

  .register-form {
    flex: 1;
    padding: 20px;

    h2 {
      text-align: center;
      margin-bottom: 15px;
    }

    form {
      display: flex;
      flex-direction: column;
      
      .input-group {
        display: flex;
        gap: 10px;
        margin-bottom: 10px;

        input {
          flex: 1;
        }
      }

      input {
        width: 100%;
        padding: 10px;
        margin-bottom: 10px;
        border: none;
        border-radius: 5px;
        background: #F4F4F4;
        color: #333;
      }

      .btn-register {
        background: $secondary-color;
        color: $text-color;
        padding: 10px;
        border: none;
        border-radius: 5px;
        cursor: pointer;
        font-weight: bold;
        transition: background 0.3s;

        &:hover {
          background: $hover-color;
        }
      }
    }

    p {
      text-align: center;
      margin-top: 10px;

      a {
        color: $secondary-color;
        text-decoration: none;

        &:hover {
          text-decoration: underline;
        }
      }
    }
  }
}
