/* Style Material dans le modal Bootstrap */
.modal-content {
    border-radius: 8px;
    overflow: hidden;
  }
  
  .material-style {
    margin-bottom: 1.5rem;
    position: relative;
  
    .mat-form-field-label {
      display: block;
      margin-bottom: 4px;
      font-size: 12px;
      color: rgba(0, 0, 0, 0.54);
      transform: translateY(1.5em);
      transition: all 0.2s;
    }
  
    .mat-input {
      width: 100%;
      padding: 8px 0;
      border: none;
      border-bottom: 1px solid #ccc;
      background-color: transparent;
      transition: border 0.2s;
  
      &:focus {
        outline: none;
        border-bottom: 2px solid #3f51b5;
        
        + .mat-form-field-label {
          color: #3f51b5;
          transform: translateY(0) scale(0.75);
        }
      }
    }
  
    .mat-error {
      color: #f44336;
      font-size: 12px;
      margin-top: 4px;
    }
  }
  
  .btn.mat-raised-button {
    background-color: #3f51b5;
    color: white;
    border: none;
    padding: 8px 16px;
    border-radius: 4px;
    text-transform: uppercase;
    font-weight: 500;
    letter-spacing: 0.5px;
  
    &:disabled {
      background-color: rgba(0, 0, 0, 0.12);
      color: rgba(0, 0, 0, 0.26);
    }
  }
