.agency-details-container {
    padding: 20px;
    font-family: 'Poppins', sans-serif;
  
    .agency-info {
      margin-bottom: 30px;
      padding: 20px;
      background: #f5f5f5;
      border-radius: 10px;
  
      h2 {
        color: #E27D60;
        margin-bottom: 20px;
      }
  
      .info-grid {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        gap: 15px;
  
        .info-item {
          display: flex;
          flex-direction: column;
  
          .label {
            font-weight: 600;
            color: #555;
            margin-bottom: 5px;
          }
  
          .value {
            color: #333;
          }
        }
      }
    }
  
    .users-section {
      .users-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 15px;
  
        h3 {
          color: #E27D60;
        }
  
        .add-user-btn {
          background: #E27D60;
          color: white;
          border-radius: 20px;
          padding: 5px 15px;
          font-weight: 500;
  
          &:hover {
            background: darken(#E27D60, 10%);
          }
        }
      }
  
      .users-table {
        width: 100%;
        overflow-x: auto;
  
        table {
          width: 100%;
          border-collapse: collapse;
  
          th, td {
            padding: 12px 15px;
            text-align: left;
            border-bottom: 1px solid #ddd;
          }
  
          th {
            background-color: #f4f4f4;
            font-weight: 600;
          }
  
          .no-users {
            text-align: center;
            padding: 20px;
            color: #777;
          }
  
          .actions {
            button {
              border: none;
              background: none;
              cursor: pointer;
              color: #F44336;
              font-size: 1.1rem;
              padding: 5px;
              border-radius: 50%;
              width: 30px;
              height: 30px;
              display: flex;
              align-items: center;
              justify-content: center;
              transition: all 0.2s;
  
              &:hover {
                background-color: rgba(244, 67, 54, 0.1);
                transform: scale(1.1);
              }
            }
          }
        }
      }
    }
  }
